def update_dict(d):
    result = {}
    for key, value in d.items():
        if isinstance(value, dict):
            result[key] = update_dict(value)
        elif isinstance(value, list):
            result[key] = [update_dict(v) for v in value]
        else:
            result[key] = eval(value)
    return result

