UITableViewCell.createCell(withIdentifier: reuseIdentifier) {
    let cell = Self.dequeueReusableCell(withIdentifier: reuseIdentifier)
    cell.textLabel?.text = "Bore"
    cell.textLabel?.textColor = .red
    cell.detailTextLabel?.text = "Do something fun"
    cell.backgroundColor = .yellow
    return cell
}
